import axios from 'axios';
import React,{useState,useEffect} from 'react'
import {useParams} from "react-router-dom"

function Edit() {
    const [name, setName] = useState("")
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState("");
    const [disc, setDisc] = useState("")
    const [image, setImage] = useState("")
    const params = useParams();

    useEffect(() => {
        axios.get(`/api/file/${params.id}`).then((res)=>{
            setName(res.data.name);
            setPrice(res.data.price);
            setCategory(res.data.category);
            setDisc(res.data.disc);
            setImage(res.data.image)
        })
    }, [])
    
    const onChangeImage = (e) => {
        setImage(e.target.files[0]);
    }

    const UpdateOnClick = (e) => {
        e.preventDefault();

        const formData = new FormData();

        formData.append("name", name);
        formData.append("category", category);
        formData.append("price", price);
        formData.append("disc", disc);
        formData.append("fileimage", image);
       
        setName("");
        setPrice("");
        setDisc("");
        setCategory("")

        axios.put(`/api/file/${params.id}`,{
            name,
            category,
            price,
            disc,
            image
        } ).then((res)=>{
            setName(res.data.name);
            setPrice(res.data.price);
            setCategory(res.data.category);
            setDisc(res.data.disc);
            setImage(res.data.image)
        })
    }


  return (
    <div>
         <form method='POST' encType='multipart/form-data' onSubmit={changeOnClick}>
                <input
                    placeholder="Name"
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="form-control" />
                     <input
                    placeholder="Category"
                    value={category}
                    type="text"
                    onChange={(e) => setCategory(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="Price"
                    value={price}
                    type="number"
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="disc"
                    type="text"
                    value={disc}
                    onChange={(e) => setDisc(e.target.value)}
                    className="form-control" />
                <input type="file"
                    filename="fileimage"
                    className="form-control-file"
                    onChange={onChangeImage}
                />{image}
                <button type="submit">Update Product</button>
            </form>

    </div>
  )
}

export default Edit
