import React, { useEffect, useState } from 'react'
import axios from "axios";


function Home() {

    const [list, setList] = useState([]);
    const [name, setName] = useState("");
    const [price, setPrice] = useState("");
    const [disc, setDisc] = useState("");
    const [image, setImage] = useState("");
    const [category, setCategory] = useState("")

    useEffect(() => {
        axios.get("/api/file").then((res) => {
            setList(res.data);
        })
    }, [])

    const onChangeImage = (e) => {
        setImage(e.target.files[0]);
    }

    const changeOnClick = (e) => {
        e.preventDefault();

        const formData = new FormData();

        formData.append("name", name);
        formData.append("category", category);
        formData.append("price", price);
        formData.append("disc", disc);
        formData.append("fileimage", image);
       

        setName("");
        setPrice("");
        setDisc("");
        setCategory("")

        axios.post("/api/file", formData).then((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err);
        })
    }
    const Delete = (id)=>{
        axios.delete(`http://localhost:5000/api/file/${id}`).then((response)=>{
          window.location.reload();
        })
       }
   

    return (
        <div>
            <form method='POST' encType='multipart/form-data' onSubmit={changeOnClick}>
                <input
                    placeholder="Name"
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="form-control" />
                     <input
                    placeholder="Cate"
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="Price"
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="Name"
                    type="text"
                    value={disc}
                    onChange={(e) => setDisc(e.target.value)}
                    className="form-control" />

                <input type="file"
                    filename="fileimage"
                    className="form-control-file"
                    onChange={onChangeImage}
                />
                <button type="submit">Post</button>
            </form>

            {list.map((item) => {
                return (
                    <div>
                        {item.name}
                        {item.price}
                        {item.disc}
                        <img src={`uploads/${item.image}`} style={{ width: "100px" }} />
                        <button onClick={()=>Delete(item._id)}>Delete</button>

                    </div>
                )
            })}


        </div>
    )
}

export default Home
